export function HOME(props) {
  let getItem = 2;
  return (
    <>
      <button onClick={()=>{
        getItem++;
        console.log(getItem);
      }}>
        버튼
      </button>
    </>
  );
}

function GET_Item(shoes, setShoes, getItem){
  console.log(getItem);
  fetch(`https://codingapple1.github.io/shop/data${getItem}.json`)
  .then((result) => result.json())
  .then((result) => {
    var temp = [...shoes, ...result];
    setShoes(temp);
    getItem++;
    })
    .catch((e)=>{
    console.log(e);
    })
}

