import './App.css';
import { useState } from 'react';

function App() {
  let blogName = "ÏßÄÎ†ÅÏù¥Ïùò ÏÑ±Ïû• Î∏îÎ°úÍ∑∏";
  let [title, changeTitle] = useState(['Ìï†Î®∏Îãà ÏΩîÌä∏ Ï∂îÏ≤ú', 'Ïó¨Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú', 'ÎÇ®Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú']);
  let [like, upLike] = useState([0,0,0]);
  let [modal, setModal] = useState(false);

  function upCntLike(i){
    var newLike = [...like];
    newLike[i] = newLike[i] + 1;
    upLike(newLike);
  }

  function order(){
    var newTitle = [...title];
    newTitle.sort(function(a, b) { // ÌïúÍ∏Ä Ïò§Î¶ÑÏ∞®Ïàú
      return a < b ? -1 : a > b ? 1 : 0;
    });
    changeTitle(newTitle);
  }

  return (
    <div className="App">
      <div className="blog-title">
        {blogName}
      </div>
      <div style={ {color : 'black', fontSize : '30px'} }>
        Í≤åÏãúÍ∏Ä
      </div>
      <button onClick={ () => { order(); } }>Ïù¥Î¶ÑÏàú Ï†ïÎ†¨</button>
      {
        title.map((t, i)=>{
          <div className="list">
            <h4 onClick={() => { modal ? setModal(false) : setModal(true); }}>{title[i]} <span onClick={ () => { upCntLike(i) } }>üëç</span> {like[i]} </h4>
            <p>2Ïõî 16Ïùº Î∞úÌñâ</p>
          </div>
        })
      }

      <div className="list">
        <h4>{title[1]} <span onClick={ () => { upCntLike(1) } }>üëç</span> {like[1]} </h4>
        <p>2Ïõî 16Ïùº Î∞úÌñâ</p>
      </div>
      <div className="list">
        <h4>{title[2]} <span onClick={ () => { upCntLike(2) } }>üëç</span> {like[2]} </h4>
        <p>2Ïõî 16Ïùº Î∞úÌñâ</p>
      </div>

      {
        modal ? <Modal /> : null
      }

    </div>
  );
}

function Modal(){
  return(
    <div className='modal'>
      <h4>Ï†úÎ™©</h4>
      <p>ÎÇ†Ïßú</p>
      <p>ÎÇ¥Ïö©</p>
    </div>
  );
}

export default App;
