import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  let blogName = "ÏßÄÎ†ÅÏù¥Ïùò ÏÑ±Ïû• Î∏îÎ°úÍ∑∏";
  let [title, changeTitle] = useState(['Ìï†Î®∏Îãà ÏΩîÌä∏ Ï∂îÏ≤ú', 'Ïó¨Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú', 'ÎÇ®Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú']);
  let [postDate, changePostDate] = useState([2,3,14,6], [3,4,12,2], [4,1,2,11]); // m:d:hh:mm
  let [like, upLike] = useState([0,0,0]);
  let [modal, setModal] = useState(0);
  let [addTitle, setAddTitle] = useState('');

  function setDate(){
    let today = new Date();

    let month = today.getMonth() + 1;  // Ïõî
    let date = today.getDate();  // ÎÇ†Ïßú
    let hours = today.getHours(); // Ïãú
    let minutes = today.getMinutes();  // Î∂Ñ
  }

  function upCntLike(i){
    var newLike = [...like];
    newLike[i] = newLike[i] + 1;
    upLike(newLike);
  }

  function order(){
    var newTitle = [...title];
    newTitle.sort(function(a, b) { // ÌïúÍ∏Ä Ïò§Î¶ÑÏ∞®Ïàú
      return a < b ? -1 : a > b ? 1 : 0;
    });
    changeTitle(newTitle);
  }

  function add(t){
    var newTitle = [...title];
    newTitle.push(t);
    changeTitle(newTitle);

    var newLike = [...like];
    newLike.push(0);
    upLike(newLike);
  }

  function remove(idx){
    console.log(idx);
    var newTitle = [...title];
    newTitle.splice(idx, 1);
    changeTitle(newTitle);

    var newLike = [...like];
    newLike.splice(idx, 1);
    upLike(newLike);

    setModal(0);
  }

  return (
    <div className="App">
      <div className="blog-title">
        {blogName}
      </div>
      <div style={ {color : 'black', fontSize : '30px'} }>
        Í≤åÏãúÍ∏Ä
      </div>
      <button onClick={ () => { order(); } }>Ïù¥Î¶ÑÏàú Ï†ïÎ†¨</button>
      {
        title.map((t, i)=>{
          console.log(t);
          return(
            <div className="list" key={i}>
              <h4 onClick={() => { modal == i + 1 ? setModal(0) : setModal(i + 1); }}>{title[i]} <span onClick={ (e) => { upCntLike(i); e.stopPropagation(); } }>üëç</span> {like[i]} </h4>
              <p>{postDate[i][0]}Ïõî {postDate[i][1]}Ïùº {postDate[i][2]}Ïãú {postDate[i][3]}Î∂Ñ Î∞úÌñâ</p>
            </div>
          )
        })
      }
      {
        modal ? <Modal color="yellow" title={title[modal - 1]} idx={modal - 1} remove={remove}/> : null
      }

      <input onChange={(e)=>{ setAddTitle(e.target.value); }}></input>
      <button onClick={()=>{ if(addTitle != "") { add(addTitle); } }}>Í∏Ä Î∞úÌñâ</button>

    </div>
  );
}

function Modal(props){
  return(
    <div className='modal' style={{background : props.color}}>
      <h4>{props.title}</h4>
      <p>ÎÇ†Ïßú</p>
      <p>ÎÇ¥Ïö©</p>
      <button onClick={()=>{props.remove(props.idx)}}>ÏÇ≠Ï†ú</button>
    </div>
  );
}

export default App;
